mapper.select:
SELECT COUNT(*) FROM R_ADDRESS R JOIN ( SELECT M.NO , M.NAME , D.NAME AS DEPT_NAME , T.NAME AS TEAM_NAME , P.POSITION AS POSITION_NAME FROM MEMBER M JOIN POSITION P ON P.NO = M.POSITION_NO JOIN DEPARTMENT D ON D.NO = M.DEPT_NO LEFT JOIN DEPARTMENT T ON T.NO = M.TEAM_NO WHERE M.RESIGNATION_YN = 'N' )M ON M.NO = R.SENDER_NO WHERE RECIVER_NO = #{no}
AND ${category} LIKE '%${keyword}%'
AND A.NAME LIKE '%${keyword}%' OR A.PHONE LIKE '%${keyword}%' OR A.EMAIL LIKE '%${keyword}%' OR A.COMPANY LIKE '%${keyword}%' OR A.POSITION LIKE '%${keyword}%' OR A.ADDRESS LIKE '%${keyword}%' OR S.NAME LIKE '%${keyword}%'

SELECT R.NO, R.RECIVER_NO, R.SENDER_NO, M.NAME AS SENDER_NAME, M.TEAM_NAME AS SENDER_TEAM, M.DEPT_NAME AS SENDER_DEPT, M.POSITION_NAME AS SENDER_POSITION, R.CHECK_YN, R.SEND_ADDRESS, R.ENROLL_DATE FROM R_ADDRESS R JOIN ( SELECT M.NO , M.NAME , D.NAME AS DEPT_NAME , T.NAME AS TEAM_NAME , P.POSITION AS POSITION_NAME FROM MEMBER M JOIN POSITION P ON P.NO = M.POSITION_NO JOIN DEPARTMENT D ON D.NO = M.DEPT_NO LEFT JOIN DEPARTMENT T ON T.NO = M.TEAM_NO WHERE M.RESIGNATION_YN = 'N' )M ON M.NO = R.SENDER_NO WHERE RECIVER_NO = #{no}
AND ${category} LIKE '%${keyword}%'
AND M.NAME LIKE '%${keyword}%' OR A.PHONE LIKE '%${keyword}%' OR A.EMAIL LIKE '%${keyword}%' OR A.COMPANY LIKE '%${keyword}%' OR A.POSITION LIKE '%${keyword}%' OR A.ADDRESS LIKE '%${keyword}%' OR S.NAME LIKE '%${keyword}%'
