mapper.select:
SELECT M.NO , M.ID , M.PWD , M.NAME , M.EMAIL , M.PHONE , M.BIRTHDAY , M.BANK_NO , B.NAME AS BANK_NAME , M.ACCOUNT , M.ADDRESS , D.NO AS DEPT_NO , D.NAME AS DEPT_NAME , T.NO AS TEAM_NO , T.NAME AS TEAM_NAME , M.POSITION_NO , P.POSITION AS POSITION_NAME , M.AUTHORIZE_NO , A.RANK AS AUTHORIZE_NAME , M.JOIN_DATE , M.MODIFY_DATE , M.RESIGNATION_YN , M.ENTRUST_NO , F.NO AS FILE_NO , F.CHANGE_NAME as FILE_NAME , F.ORIGIN_NAME AS FILE_ORIGIN_NAME FROM MEMBER M JOIN BANK B ON B.NO = M.BANK_NO JOIN POSITION P ON P.NO = M.POSITION_NO JOIN AUTHORIZE A ON A.NO = M.AUTHORIZE_NO JOIN DEPARTMENT D ON D.NO = M.DEPT_NO LEFT JOIN DEPARTMENT T ON T.NO = M.TEAM_NO LEFT JOIN ( SELECT "NO", "ORIGIN_NAME", "MEMBER_NO", "CHANGE_NAME", "FILE_PATH", "ENROLL_DATE", "THUMB_YN" FROM PROFILE_A WHERE "THUMB_YN" = 'Y' ) F ON M.NO = F.MEMBER_NO WHERE M.ID = #{id}
AND M.PWD = #{pwd}
AND M.RESIGNATION_YN = 'N'

mapper.insert:
INSERT INTO
MEMBER
(
	"NO" 
	, "ID"
	, "PWD"
	, "NAME"
	, "EMAIL"
	, "PHONE"
	, "BIRTHDAY"
	, "BANK_NO"
	, "ACCOUNT"
	, "ADDRESS"
	, "DEPT_NO"
	, "TEAM_NO"
	, "POSITION_NO"
	, "AUTHORIZE_NO"
	, "JOIN_DATE"
	, "MODIFY_DATE"
	, "RESIGNATION_YN"
	, "ENTRUST_NO"
)
VALUES 
(
	SEQ_MEMBER_NO.NEXTVAL
	, #{id}
	, #{pwd}
	, #{name}
	, #{email}
	, #{phone}
	, #{birthday}
	, #{bankNo}
	, #{account}
	, #{address}
	, #{deptNo}
	, #{teamNo}
	, #{positionNo}
	, #{authorizeNo}
	, SYSDATE
	, SYSDATE
	, 'N'
	, #{entrustNo}
)
