mapper.insert:
    	INSERT INTO PROJECT
			(
			    NO
			    , LEADER
			    , WRITER
			    , TITLE
			    , CONTENT
			    , START_DATE
			    , END_DATE
			)
			VALUES
			(
			    SEQ_PROJECT_NO.NEXTVAL
			    , #{leader}
			    , #{writer}
			    , #{title}
			    , #{content}
			    , #{startDate}
			    , #{endDate}
			)

mapper.select:
    	SELECT 
		    NO 
		FROM PROJECT 
		WHERE LEADER = #{leader}
		AND WRITER = #{writer}
		AND TITLE = #{title}
		AND END_DATE = #{endDate}

mapper.update:
UPDATE PROJECT
SET
	STATUS = #{status}
	, PROGRESS = #{progress}
	, CONTENT = #{content}
WHERE NO = #{no}

UPDATE PROJECT 
SET FAVORITES_YN = 'Y'  
WHERE NO = #{no}

UPDATE PROJECT
SET FAVORITES_YN = 'N'
WHERE NO = #{no}

